import Header from 'components/Header'
import BookItem from 'components/BookItem';
import bookData from 'utils/books.json'
import './index.css';
import './App.css';
import Carousel from 'components/Carousel';


function App() {
  const {data: books}=bookData;


  return (
    <>
    <div style={{width:'100%',height:"100%",top:"0px",left:"0px",position:"fixed",background:"rgba(0,0,0,0.5",display:"flex",justifyContent:"flex-end",zIndex:"11"}}>
      <section style={{width:"480px",borderColor:"1px solid red",padding:"70px 20px 20px 20px"}}>
        <div style={{height:"100%",width:"100%", background:"#fff"}} >
        vowgkvrokop
        </div>
      </section>

    </div>
    <div>
     <Header/>
     <main className='main'>
       <div className='featured-h6'>
        <h6 className='h6'>Featured Books</h6>
      </div>
      <Carousel/>
      <div className='featured-h6'>
        <h6 className='h6'>All Books</h6>
      </div>
      <section className='book-items-wrapper'>
        {books.map((book,i)=>  <BookItem  key={i} book={book}/>)}
      </section>
     </main>
    </div>
    </>
  );
}

export default App;
