import {Book} from 'interfaces'


interface Action{
	type:string;
	[extraField:string]:any
}

const bookReducer = (state:Book, action:Action) => {
	switch (action.type) {
	  case 'ADD_BOOK':
		return {  };
	  case 'REMOVE_BOOK':
		return { };

	  default:
		return state;
	}
  };

  export default bookReducer;
