import Header from 'components/Header'
import BookItem from 'components/BookItem';
import bookData from 'utils/books.json'
import './index.css';
import './App.css';
import Carousel from 'components/Carousel';


function App() {
  const {data: books}=bookData;


  return (
    <>
    <div className="cart-overlay">
      <section className="cart-box" >
        <div style={{height:"100%",width:"100%"}} >
          <article style={{height:"150px",padding:"1px solid blue",border:"1px solid blue",display:"flex",justifyContent:"space-between"}}>
dd
          </article>
        </div>
      </section>

    </div>
    <div>
     <Header/>
     <main className='main'>
       <div className='featured-h6'>
        <h6 className='h6'>Featured Books</h6>
      </div>
      <Carousel/>
      <div className='featured-h6'>
        <h6 className='h6'>All Books</h6>
      </div>
      <section className='book-items-wrapper'>
        {books.map((book,i)=>  <BookItem  key={i} book={book}/>)}
      </section>
     </main>
    </div>
    </>
  );
}

export default App;
